import React, {useState} from "react";
import "./index.css";

export default function App() {
  const [values,setValues] = useState({
    firstName:"",
    lastName:"",
    email:"",
  });

  const [submitted,setSubmiited] = useState(false);

  const handleFirstNameInputChange =(event) => {
    setValues({...values, firstName: event.target.value})
  }

  const handleLastNameInputChange =(event) => {
    setValues({...values, lastName: event.target.value})
  }

  const handleEmailNameInputChange =(event) => {
    setValues({...values, email: event.target.value})
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    setSubmiited(true);
  }




  return (
    <div class="form-container">
      <form class="register-form" onSubmit={handleSubmit}>
       {submitted ? <div class="success-message">Success! Thank you for registering</div> : null }
        <input value={values.firstName}
          onChange = {handleFirstNameInputChange}
          id="first-name"
          class="form-field"
          type="text"
          placeholder="First Name"
          name="firstName"
        />

        <span id="first-name-error">Please enter a first name</span> 
        <input value={values.lastName}
          onChange = {handleLastNameInputChange}
          id="last-name"
          class="form-field"
          type="text"
          placeholder="Last Name"
          name="lastName"
        />

        <span id="last-name-error">Please enter a last name</span> 
        <input value={values.email}
          onChange = {handleEmailNameInputChange}
          id="email"
          class="form-field"
          type="text"
          placeholder="Email"
          name="email"
        />

        <span id="email-error">Please enter an email address</span> 
        <button class="form-field" type="submit">
          Register
        </button>
      </form>
    </div>
  );
}
